const input = $input.first().json.body.data.message.conversation || "";

// Match either @"group name with spaces" or @groupname followed by the message
const match = input.match(/^@(?:"([^"]+)"|(\S+))\s+(.+)/);

if (match) {
  const recipient = match[1] || match[2];
  const message = match[3];

  return [
    {
      json: {
        valid: true,
        remoteJid:$input.first().json.body.data.key.remoteJid,
        recipient,
        message,
        original: input,
      },
    },
  ];
} else {
  return [
    {
      json: {
        valid: false,
        error: 'Invalid format. Use: @"group name" message OR @groupname message',
        original: input,
      },
    },
  ];
}

const recipient = $('Code').first().json.recipient
const message = $('Code').first().json.message

// 3. Sample group list (replace this with actual data)
const groups = $('HTTP Request').all()

const matchedGroup = groups.find(group => group.json.subject === recipient);

if (matchedGroup) {
  return [{
    json: {
      valid: true,
      recipientName: recipient,
      message,
      groupId: matchedGroup.json.id,
      groupName: matchedGroup.json.subject
    }
  }];
} else {
  return [{
    json: {
      valid: false,
      error: `Group "${recipient}" not found`,
      availableGroups: groups.map(g => g.json.subject),
    }
  }];
}

const records = $input.first().json.messages.records || [];

const messages = records
  .map(item => {
    const text = item.message?.conversation ||
                 item.message?.protocolMessage?.editedMessage?.conversation ||
                 null;
    return text ? { text } : null;
  })
  .filter(Boolean).slice(0, 20) 


const context = messages.map(msg => msg.text.replace(/\n/g, " ")).join("\n");


return [{ json: { context } }];
